mapper.select:
SELECT cartNo, memberId, c.goodsNo, cartCount
FROM tbl_cart c LEFT JOIN tbl_goods g
ON c.goodsNo = g.goodsNo
WHERE memberId = #{memberId} 
AND c.goodsNo = #{goodsNo}

SELECT count(*)
FROM tbl_cart
WHERE memberId = #{memberId}

mapper.update:
UPDATE tbl_cart
SET cartCount = #{cartCount}
WHERE cartNo = #{cartNo}
