mapper.select:
SELECT goodsNo,goodsCategory,goodsName,goodsContent,goodsPrice,goodsImage FROM ( SELECT rownum rn,goodsNo,goodsCategory,goodsName,goodsContent,goodsPrice,goodsImage FROM (SELECT goodsNo,goodsCategory,goodsName,goodsContent,goodsPrice,goodsImage,categoryTitle FROM tbl_goods g LEFT JOIN tbl_category c ON g.goodsCategory = c.categorySubTitle
ORDER BY goodsPrice ASC
ORDER BY goodsPrice DESC
ORDER BY goodsregDate DESC
) WHERE categoryTitle = #{goodsCategory} AND rownum <= #{cri.pageNum} * #{cri.amount} ) WHERE rn > (#{cri.pageNum} -1) * #{cri.amount}

SELECT goodsNo,goodsCategory,goodsName,goodsContent,goodsPrice,goodsImage,categoryTitle FROM ( SELECT rownum rn,goodsNo,goodsCategory,goodsName,goodsContent,goodsPrice,goodsImage,categoryTitle FROM (SELECT goodsNo,goodsCategory,goodsName,goodsContent,goodsPrice,goodsImage,categoryTitle FROM tbl_goods g LEFT JOIN tbl_category c ON g.goodsCategory = c.categorySubTitle
ORDER BY goodsPrice ASC
ORDER BY goodsPrice DESC
ORDER BY goodsregDate DESC
) WHERE goodsCategory = #{categorySubTitle} AND rownum <= #{cri.pageNum} * #{cri.amount} ) WHERE rn > (#{cri.pageNum} -1) * #{cri.amount}
