mapper.select:

SELECT *
FROM ( SELECT /*+ INDEX_DESC (tbl_goods pk_goods) */
	   rownum rn,goodsNo,goodsCategory,goodsName,goodsContent,goodsPrice
       ,goodsUnit,goodsWeight,goodsDelivery,goodsPackage,goodsAllergy,goodsImage
       FROM tbl_goods g LEFT JOIN ON tbl_category c
       ON g.goodsCategory = c.categorySubTitle  
       WHERE rownum <= #{cri.pageNum} * #{cri.amount} 
      )
WHERE rn > (#{cri.pageNum} -1) * #{cri.amount} 
AND categoryTitle = #{goods.goodsCategory}
